  <script>
    let editingNameId = null;

    // API helper function
    async function api(path, opts = {}) {
      const config = {
        headers: {
          'Content-Type': 'application/json',
        },
        ...opts
      };

      try {
        const res = await fetch(path, config);
        const contentType = res.headers.get('content-type') || '';

        if (contentType.includes('application/json')) {
          const data = await res.json();
          if (!res.ok) throw data;
          return data;
        } else {
          if (!res.ok) throw { error: 'Unexpected response' };
          return null;
        }
      } catch (error) {
        console.error('API Error:', error);
        throw error;
      }
    }

    // Utility functions
    function showMsg(message, type = 'error') {
      const el = document.getElementById('namesMsg');
      el.textContent = message;
      el.className = `msg ${type}`;
      el.style.display = 'block';
      setTimeout(() => {
        el.style.display = 'none';
      }, 5000);
    }

    function escapeHtml(unsafe) {
      return unsafe
        .replace(/&/g, '&amp;')
        .replace(/</g, '&lt;')
        .replace(/>/g, '&gt;')
        .replace(/"/g, '&quot;')
        .replace(/'/g, '&#039;');
    }

    function formatDateTime(dateString) {
      if (!dateString) return '';
      return new Date(dateString).toLocaleString('zh-TW');
    }

    function updateStats(count) {
      const statsEl = document.getElementById('statsText');
      statsEl.textContent = `總共有 ${count} 個名字`;
    }

    // Names management functions
    async function loadNames() {
      const loadingDiv = document.getElementById('loadingDiv');
      const namesList = document.getElementById('namesList');

      try {
        loadingDiv.style.display = 'block';
        namesList.style.display = 'none';

        const data = await api('/api/names', { method: 'GET' });
        const ul = document.getElementById('namesList');
        ul.innerHTML = '';

        updateStats(data.length);

        if (data.length === 0) {
          ul.innerHTML = `
            <div class="empty-state">
              <h3>還沒有任何名字</h3>
              <p>開始新增您的第一個名字吧！</p>
            </div>
          `;
        } else {
          data.forEach(item => {
            const li = document.createElement('li');
            li.innerHTML = `
              <div class="item-header">
                <h4 class="item-title">${escapeHtml(item.name)}</h4>
                <div class="item-actions">
                  <button onclick="editName(${item.id}, '${escapeHtml(item.name).replace(/'/g, "\\'")}')">編輯</button>
                  <button class="danger" onclick="deleteName(${item.id})">刪除</button>
                </div>
              </div>
              <div class="meta">
                ID: ${item.id} | 建立時間: ${formatDateTime(item.created_at)}
              </div>
            `;
            ul.appendChild(li);
          });
        }

        loadingDiv.style.display = 'none';
        namesList.style.display = 'block';
      } catch (e) {
        loadingDiv.style.display = 'none';
        namesList.style.display = 'block';
        console.error('Load names error:', e);
        showMsg(e.detail || e.error || '載入名字列表失敗', 'error');
        updateStats(0);
      }
    }

    async function addName() {
      const nameInput = document.getElementById('nameInput');
      const name = nameInput.value.trim();

      if (!name) {
        showMsg('名字不能為空', 'error');
        nameInput.focus();
        return;
      }

      if (name.length > 50) {
        showMsg('名字長度超過 50 字元', 'error');
        nameInput.focus();
        return;
      }

      try {
        await api('/api/names', {
          method: 'POST',
          body: JSON.stringify({ name })
        });

        nameInput.value = '';
        showMsg('名字已新增', 'success');
        loadNames();
      } catch (e) {
        console.error('Add name error:', e);
        showMsg(e.detail || e.error || '新增名字失敗', 'error');
      }
    }

    function editName(id, currentName) {
      editingNameId = id;
      document.getElementById('nameInput').value = currentName;
      document.getElementById('addNameBtn').style.display = 'none';
      document.getElementById('editNameBtn').style.display = 'inline-block';
      document.getElementById('cancelEditBtn').style.display = 'inline-block';
      document.getElementById('nameInput').focus();
    }

    async function updateName() {
      const nameInput = document.getElementById('nameInput');
      const name = nameInput.value.trim();

      if (!name) {
        showMsg('名字不能為空', 'error');
        nameInput.focus();
        return;
      }

      if (name.length > 50) {
        showMsg('名字長度超過 50 字元', 'error');
        nameInput.focus();
        return;
      }

      try {
        await api(`/api/names/${editingNameId}`, {
          method: 'PUT',
          body: JSON.stringify({ name })
        });

        cancelEdit();
        showMsg('名字已更新', 'success');
        loadNames();
      } catch (e) {
        console.error('Update name error:', e);
        showMsg(e.detail || e.error || '更新名字失敗', 'error');
      }
    }

    function cancelEdit() {
      editingNameId = null;
      document.getElementById('nameInput').value = '';
      document.getElementById('addNameBtn').style.display = 'inline-block';
      document.getElementById('editNameBtn').style.display = 'none';
      document.getElementById('cancelEditBtn').style.display = 'none';
    }

    async function deleteName(id) {
      if (!confirm('確定要刪除此名字？')) return;

      try {
        await api(`/api/names/${id}`, { method: 'DELETE' });
        showMsg('名字已刪除', 'success');
        loadNames();
      } catch (e) {
        console.error('Delete name error:', e);
        showMsg(e.detail || e.error || '刪除名字失敗', 'error');
      }
    }

    // Event listeners
    document.getElementById('addNameBtn').addEventListener('click', addName);
    document.getElementById('editNameBtn').addEventListener('click', updateName);
    document.getElementById('cancelEditBtn').addEventListener('click', cancelEdit);

    // Enter key support
    document.getElementById('nameInput').addEventListener('keypress', (e) => {
      if (e.key === 'Enter') {
        if (editingNameId) {
          updateName();
        } else {
          addName();
        }
      }
    });

    // Initial load
    loadNames();
  </script>
