      <button id="addNameBtn">新增名字</button>

      <div id="namesMsg" class="msg"></div>
    </div>

    <div class="section">
      <h2>📋 名字列表</h2>
      <div id="loadingIndicator" class="loading" style="display: none;"></div>
      <ul id="namesList"></ul>
    </div>
