  <script>
    // API基礎URL (將由Nginx代理到backend)
    const API_BASE = '/api';

    // 全域變數
    let namesData = [];

    // 工具函數
    function showMsg(message, type = 'error') {
      const el = document.getElementById('namesMsg');
      el.textContent = message;
      el.className = `msg ${type}`;
      el.style.display = 'block';
      setTimeout(() => {
        el.style.display = 'none';
      }, 4000);
    }

    function escapeHtml(unsafe) {
      return unsafe
        .replaceAll('&', '&amp;')
        .replaceAll('<', '&lt;')
        .replaceAll('>', '&gt;')
        .replaceAll('"', '&quot;')
        .replaceAll("'", '&#039;');
    }

    function formatDateTime(dateString) {
      if (!dateString) return '';
      const date = new Date(dateString);
      return date.toLocaleString('zh-TW', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    }

    // API函數
    async function apiCall(endpoint, options = {}) {
      try {
        const response = await fetch(`${API_BASE}${endpoint}`, {
          headers: {
            'Content-Type': 'application/json',
            ...options.headers
          },
          ...options
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({ detail: '未知錯誤' }));
          throw new Error(errorData.detail || `HTTP ${response.status}`);
        }

        return await response.json();
      } catch (error) {
        console.error('API 呼叫錯誤:', error);
        throw error;
      }
    }

    // 檢查系統健康狀態
    async function checkHealth() {
      try {
        await apiCall('/health');
        document.getElementById('statusIndicator').textContent = '🟢';
        return true;
      } catch (error) {
        document.getElementById('statusIndicator').textContent = '🔴';
        console.error('健康檢查失敗:', error);
        return false;
      }
    }

    // 載入名字列表
    async function loadNames() {
      const loadingEl = document.getElementById('loadingIndicator');
      const listEl = document.getElementById('namesList');

      loadingEl.style.display = 'block';

      try {
        const data = await apiCall('/names');
        namesData = data;

        // 更新總數統計
        document.getElementById('totalCount').textContent = data.length;

        // 渲染列表
        if (data.length === 0) {
          listEl.innerHTML = `
            <div class="empty-state">
              <div class="empty-state-icon">📝</div>
              <p>目前沒有任何名字</p>
              <p>開始新增第一個名字吧！</p>
            </div>
          `;
        } else {
          listEl.innerHTML = data.map(item => `
            <li>
              <div class="item-header">
                <h4 class="item-title">${escapeHtml(item.name)}</h4>
                <div class="item-actions">
                  <button class="danger" onclick="deleteName(${item.id})">刪除</button>
                </div>
              </div>
              <div class="meta">
                ID: ${item.id} • 建立時間: ${formatDateTime(item.created_at)}
              </div>
            </li>
          `).join('');
        }
      } catch (error) {
        showMsg(`載入名字列表失敗: ${error.message}`, 'error');
        listEl.innerHTML = `
          <div class="empty-state">
            <div class="empty-state-icon">⚠️</div>
            <p>載入失敗</p>
            <p>請檢查網路連線或稍後再試</p>
          </div>
        `;
      } finally {
        loadingEl.style.display = 'none';
      }
    }

    // 新增名字
    async function addName() {
      const nameInput = document.getElementById('nameInput');
      const name = nameInput.value.trim();

      if (!name) {
        showMsg('名字不能為空', 'error');
        nameInput.focus();
        return;
      }

      if (name.length > 50) {
        showMsg('名字長度不能超過50字元', 'error');
        nameInput.focus();
        return;
      }

      try {
        await apiCall('/names', {
          method: 'POST',
          body: JSON.stringify({ name })
        });

        nameInput.value = '';
        showMsg('名字新增成功！', 'success');
        await loadNames(); // 重新載入列表
      } catch (error) {
        showMsg(`新增名字失敗: ${error.message}`, 'error');
      }
    }

    // 刪除名字
    async function deleteName(id) {
      if (!confirm('確定要刪除這個名字嗎？')) {
        return;
      }

      try {
        await apiCall(`/names/${id}`, {
          method: 'DELETE'
        });

        showMsg('名字刪除成功！', 'success');
        await loadNames(); // 重新載入列表
      } catch (error) {
        showMsg(`刪除名字失敗: ${error.message}`, 'error');
      }
    }

    // 事件監聽器
    document.getElementById('addNameBtn').addEventListener('click', addName);

    document.getElementById('nameInput').addEventListener('keypress', (e) => {
      if (e.key === 'Enter') {
        addName();
      }
    });

    // 字元計數顯示
    document.getElementById('nameInput').addEventListener('input', (e) => {
      const length = e.target.value.length;
      const label = e.target.previousElementSibling;
      label.textContent = `輸入名字 (${length}/50)`;

      if (length > 45) {
        e.target.style.borderColor = '#e74c3c';
      } else {
        e.target.style.borderColor = '#e1e8ed';
      }
    });

    // 初始化
    async function init() {
      console.log('Worker 名字管理系統啟動中...');

      // 檢查健康狀態
      const isHealthy = await checkHealth();

      if (isHealthy) {
        // 載入名字列表
        await loadNames();

        // 設定定期健康檢查
        setInterval(checkHealth, 30000); // 每30秒檢查一次

        console.log('系統初始化完成');
      } else {
        showMsg('無法連接到後端服務，請檢查系統狀態', 'error');
      }
    }

    // 頁面載入完成後初始化
    document.addEventListener('DOMContentLoaded', init);
  </script>
