{"content": "# Worker 系統測試腳本

Write-Host "========================================" -ForegroundColor Cyan
Write-Host "Worker 名字管理系統 - 功能測試" -ForegroundColor Cyan
Write-Host "========================================" -ForegroundColor Cyan

$baseUrl = "http://localhost:8080"
$apiUrl = "$baseUrl/api"

# 測試函數
function Test-Endpoint {
    param(
        [string]$Url,
        [string]$Method = "GET",
        [string]$Body = $null,
        [string]$Description
    )

    Write-Host "🔍 測試: $Description" -ForegroundColor Yellow

    try {
        $headers = @{ "Content-Type" = "application/json" }

        if ($Body) {
            $response = Invoke-RestMethod -Uri $Url -Method $Method -Body $Body -Headers $headers
        } else {
            $response = Invoke-RestMethod -Uri $Url -Method $Method -Headers $headers
        }

        Write-Host "✅ 成功: $($response | ConvertTo-Json -Compress)" -ForegroundColor Green
        return $response
    } catch {
        Write-Host "❌ 失敗: $($_.Exception.Message)" -ForegroundColor Red
        return $null
    }
}

# 等待用戶確認服務已啟動
Write-Host "請確認服務已經啟動並運行在 $baseUrl" -ForegroundColor Yellow
$confirm = Read-Host "按 Enter 繼續測試，或輸入 'q' 退出"
if ($confirm -eq "q") {
    exit
}

Write-Host "`n開始功能測試..." -ForegroundColor Cyan

# 1. 測試前端首頁
Write-Host "`n--- 前端測試 ---" -ForegroundColor Magenta
try {
    $response = Invoke-WebRequest -Uri $baseUrl -Method GET
    if ($response.StatusCode -eq 200) {
        Write-Host "✅ 前端首頁載入成功" -ForegroundColor Green
    }
} catch {
    Write-Host "❌ 前端首頁載入失敗: $($_.Exception.Message)" -ForegroundColor Red
}

# 2. 測試後端健康檢查
Write-Host "`n--- 後端API測試 ---" -ForegroundColor Magenta
Test-Endpoint -Url "$apiUrl/health" -Description "健康檢查"

# 3. 測試獲取名字列表（初始為空）
$names = Test-Endpoint -Url "$apiUrl/names" -Description "獲取名字列表"

# 4. 測試新增名字
$testName1 = @{ name = "測試名字1" } | ConvertTo-Json -Compress
$newName1 = Test-Endpoint -Url "$apiUrl/names" -Method "POST" -Body $testName1 -Description "新增第一個名字"

$testName2 = @{ name = "測試名字2" } | ConvertTo-Json -Compress
$newName2 = Test-Endpoint -Url "$apiUrl/names" -Method "POST" -Body $testName2 -Description "新增第二個名字"

# 5. 測試獲取名字總數
Test-Endpoint -Url "$apiUrl/names/count" -Description "獲取名字總數"

# 6. 再次獲取名字列表
$updatedNames = Test-Endpoint -Url "$apiUrl/names" -Description "獲取更新後的名字列表"

